class IdentifierDocLinkedDomainsServiceEndpointSerializerTest {

    @Test
    fun `test linked domains with single domain as string failing when endpoint type is missing`() {
        val didDocument =
            """{"@context":"https://www.w3.org/ns/did-resolution/v1","didDocument":{"id":"did:ion:EiC5-1uBg-YC2DvQRbI6eihDvk7DOYaQ08OB0I3jCe9Ydg:","@context":["https://www.w3.org/ns/did/v1",{"@base":"did:ion:EiC5-1uBg-YC2DvQRbI6eihDvk7DOYaQ08OB0I3jCe9Ydg:"}],"service":[{"id":"#anyServiceEndpointId","serviceEndpoint":"http://any.endpoint"}],"publicKey":[{"id":"#anySigningKeyId","controller":"","type":"EcdsaSecp256k1VerificationKey2019","publicKeyJwk":{"crv":"secp256k1","kty":"EC","x":"aGsMG0u9FX6I54peIKqYokjnQPGhLUYTOQNc3nOvE1Q","y":"fjilqheWQYkHNE70shMRyMDrZp8EGCfE_aL3h-yJmQA"}}],"authentication":["#anySigningKeyId"]},"methodMetadata":{"published":false,"recoveryCommitment":"EiBC4hS1UGySgNf3maLvsJEJq_NZAIJkJgu3J12LxcDHOw","updateCommitment":"EiDFC6DODwBMxnd__h11Rx4Nn1e8znlYOR2a-PjyCokcFg"},"resolverMetadata":{"driverId":"did:ion","driver":"HttpDriver","retrieved":"2020-10-20T05:52:41.693Z","duration":"126.6567ms"}}"""
        Assertions.assertThatThrownBy { defaultTestSerializer.decodeFromString(IdentifierResponse.serializer(), didDocument) }
            .isInstanceOf(kotlinx.serialization.SerializationException::class.java)
    }

    @Test
    fun `test if service endpoints with invalid key returns empty list`() {
        val didDocumentMapArray =
            """{"@context":"https://www.w3.org/ns/did-resolution/v1","didDocument":{"id":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","@context":["https://www.w3.org/ns/did/v1",{"@base":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:"}],"service":[{"id":"#anyServiceEndpointId","type":"LinkedDomains","serviceEndpoint": { "invalidType": [ "someValue1", "someValue2" ] }}],"verificationMethod":[{"id":"#RU4_sign_ION_1","controller":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","type":"EcdsaSecp256k1VerificationKey2019","publicKeyJwk":{"crv":"secp256k1","kty":"EC","x":"3lNU_20ntDxp_fpgl4LPGmaG8sC8O-v7P2vNphm4GpU","y":"LsIIx_YNwIlgPCjclnIARs-Q8mJW7gC492p3Pw9gAZM"}}],"authentication":["did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:#RU4_sign_ION_1"]},"methodMetadata":{"published":false,"recoveryCommitment":"EiDNWAIs4rx7yjQKQLFQ1gc1MIkH1PETj7yCZX66rmjz5A","updateCommitment":"EiD4fd7FQM8ZkS0_qT5okF9ORwzqKH6EIPzYdgShHc88_g"},"resolverMetadata":{"duration":403.544624,"identifier":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","driverId":"discover.did.microsoft.com/did:ion","didUrl":{"didUrlString":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","did":{"didString":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","method":"ion","methodSpecificId":"EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","parseTree":null,"parseRuleCount":null},"parameters":null,"parametersMap":{},"path":"","query":null,"fragment":null,"parseTree":null,"parseRuleCount":null}}}"""
        val identifierResponse = defaultTestSerializer.decodeFromString(IdentifierResponse.serializer(), didDocumentMapArray)
        assertThat(identifierResponse.didDocument.service.first().serviceEndpoint).isEmpty()
    }

    @Test
    fun `test if service endpoints with case insensitive key parses successfully`() {
        val didDocumentMapArray =
            """{"@context":"https://www.w3.org/ns/did-resolution/v1","didDocument":{"id":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","@context":["https://www.w3.org/ns/did/v1",{"@base":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:"}],"service":[{"id":"#anyServiceEndpointId","type":"LinkedDomains","serviceEndpoint": { "Origins": [ "someValue1", "someValue2" ] }}],"verificationMethod":[{"id":"#RU4_sign_ION_1","controller":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","type":"EcdsaSecp256k1VerificationKey2019","publicKeyJwk":{"crv":"secp256k1","kty":"EC","x":"3lNU_20ntDxp_fpgl4LPGmaG8sC8O-v7P2vNphm4GpU","y":"LsIIx_YNwIlgPCjclnIARs-Q8mJW7gC492p3Pw9gAZM"}}],"authentication":["did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:#RU4_sign_ION_1"]},"methodMetadata":{"published":false,"recoveryCommitment":"EiDNWAIs4rx7yjQKQLFQ1gc1MIkH1PETj7yCZX66rmjz5A","updateCommitment":"EiD4fd7FQM8ZkS0_qT5okF9ORwzqKH6EIPzYdgShHc88_g"},"resolverMetadata":{"duration":403.544624,"identifier":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","driverId":"discover.did.microsoft.com/did:ion","didUrl":{"didUrlString":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","did":{"didString":"did:ion:EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","method":"ion","methodSpecificId":"EiBFVG2BhADKN48GNNhD-y0EgrcjluWJg7tQbpe1rv6kMQ:","parseTree":null,"parseRuleCount":null},"parameters":null,"parametersMap":{},"path":"","query":null,"fragment":null,"parseTree":null,"parseRuleCount":null}}}"""
        val identifierResponse = defaultTestSerializer.decodeFromString(IdentifierResponse.serializer(), didDocumentMapArray)
        assertThat(identifierResponse.didDocument.service.first().serviceEndpoint.contains("someValue1")).isTrue()
        assertThat(identifierResponse.didDocument.service.first().serviceEndpoint.contains("someValue2")).isTrue()
    }

