import com.microsoft.walletlibrary.did.sdk.util.Constants.DEFAULT_EXPIRATION_IN_SECONDS
import kotlin.Result as KotlinResult
import com.microsoft.walletlibrary.did.sdk.util.controlflow.Result
import io.mockk.coEvery
import io.mockk.every
import io.mockk.mockk
import io.mockk.mockkConstructor
import io.mockk.spyk
import io.mockk.verify
import kotlinx.coroutines.runBlocking
import org.assertj.core.api.Assertions.assertThat
import org.junit.Test

class IssuanceServiceTest {

    private val identifierService: IdentifierService = mockk()
    private val masterIdentifier: Identifier = mockk()

    private val mockedResolver: Resolver = mockk()
    private val mockedJwtValidator: JwtValidator = mockk()
    private val issuanceResponseFormatter: IssuanceResponseFormatter = mockk()
    private val mockedJwtDomainLinkageCredentialValidator = JwtDomainLinkageCredentialValidator(mockedJwtValidator, defaultTestSerializer)
    private val linkedDomainsService =
        spyk(LinkedDomainsService(mockk(relaxed = true), mockedResolver, mockedJwtDomainLinkageCredentialValidator))
    private val issuanceService =
        spyk(
            IssuanceService(
                identifierService,
                linkedDomainsService,
                mockk(relaxed = true),
                mockedJwtValidator,
                issuanceResponseFormatter,
                defaultTestSerializer
            ),
            recordPrivateCalls = true
        )

    private val expectedContractString =
        """{"id":"BusinessCard","display":{"id":"display","locale":"en-US","contract":"BusinessCard","card":{"title":"Business Card","issuedBy":"Adatum Corporation","backgroundColor":"#FFBD02","textColor":"#000000","logo":{"uri":"https://test-relyingparty.azurewebsites.net/images/adatum_corp.png","description":"Adatum Corp Logo"},"description":"This is your business card."},"consent":{"title":"Do you want to get your personal business Card?","instructions":"You will need to present your name and business name in order to get this card."},"claims":{"vc.credentialSubject.firstName":{"type":"String","label":"First Name"},"vc.credentialSubject.lastName":{"type":"String","label":"Last Name"},"vc.credentialSubject.businessName":{"type":"String","label":"Business"}}},"input":{"id":"input","credentialIssuer":"portableIdentities/card/issue","issuer":"did:ion:EiCfeOciEjwupwRQsJC3wMZzz3_M3XIo6bhy7aJkCG6CAQ?-ion-initial-state=eyJkZWx0YV9oYXNoIjoiRWlEMDQwY2lQakUxR0xqLXEyWmRyLVJaXzVlcU8yNFlDMFI5bTlEd2ZHMkdGQSIsInJlY292ZXJ5X2NvbW1pdG1lbnQiOiJFaUMyRmQ5UE90emFNcUtMaDNRTFp0Wk43V0RDRHJjdkN4eTNvdlNERDhKRGVRIn0.","attestations":{"selfIssued":{"encrypted":false,"claims":[{"claim":"first_name","type":"String","required":false,"indexed":false},{"claim":"last_name","type":"String","required":false,"indexed":false},{"claim":"business","type":"String","required":false,"indexed":false}],"required":false}}}}"""
    private val expectedContractJwt =
        "eyJhbGciOiJFUzI1NksiLCJraWQiOiJkaWQ6aW9uOkVpQ2ZlT2NpRWp3dXB3UlFzSkMzd01aenozX00zWElvNmJoeTdhSmtDRzZDQVE_.__.jvDa7enSTV2jKJIweVFfJuSbek0Qe5yOc9p1sBCXeQgWOCv5WDr_LeE5akEi-1Wa6QSjcdOI-8Lx6fhAQMw0Hg"
    private val expectedContract: VerifiableCredentialContract
    private val suppliedVcJti = "testJti"
    private val suppliedVcRaw = "testVcRaw"
    private val suppliedVcSubject = "subject"
    private val suppliedVcIssuer = "Issuer"
    private val suppliedIssuedTime = 12345678L
    private val suppliedExpirationTime = 145678998L
    private val suppliedVcContent = VerifiableCredentialContent(
        suppliedVcJti,
        VerifiableCredentialDescriptor(listOf("contexts"), listOf("credentialTypes"), mapOf("credSubKey" to "credSubValue")),
        suppliedVcSubject,
        suppliedVcIssuer,
        suppliedIssuedTime,
        suppliedExpirationTime
    )
    private val expectedVerifiableCredential =
        VerifiableCredential(
            suppliedVcJti,
            suppliedVcRaw,
            suppliedVcContent
        )
    private val formattedResponse = "FORMATTED_RESPONSE"
    private val mockedIdentifierDocument: IdentifierDocument = mockk()
    private val mockedIdentifierDocumentService: IdentifierDocumentService = mockk()
    private val mockedIdentifierDocumentServiceEndpoint = "testserviceendpoint.com"
    private val mockedIdentifierDocumentServiceType = "LinkedDomains"

    init {
        coEvery { identifierService.getMasterIdentifier() } returns Result.Success(masterIdentifier)
        mockkConstructor(FetchContractNetworkOperation::class)
        expectedContract = setUpTestContract(expectedContractString)
        mockkConstructor(SendVerifiableCredentialIssuanceRequestNetworkOperation::class)
    }

    private fun setUpTestContract(expectedContractJwt: String): VerifiableCredentialContract {
        return defaultTestSerializer.decodeFromString(VerifiableCredentialContract.serializer(), expectedContractJwt)
    }

    @Test
    fun `test to get Issuance Request with linked domains unverified`() {
        val suppliedContractUrl = "BusinessCard"
        val expectedEntityName = "Adatum Corporation"
        val expectedEntityIdentifier =
            "did:ion:EiCfeOciEjwupwRQsJC3wMZzz3_M3XIo6bhy7aJkCG6CAQ?-ion-initial-state=eyJkZWx0YV9oYXNoIjoiRWlEMDQwY2lQakUxR0xqLXEyWmRyLVJaXzVlcU8yNFlDMFI5bTlEd2ZHMkdGQSIsInJlY292ZXJ5X2NvbW1pdG1lbnQiOiJFaUMyRmQ5UE90emFNcUtMaDNRTFp0Wk43V0RDRHJjdkN4eTNvdlNERDhKRGVRIn0."

        coEvery { issuanceService["fetchContract"](suppliedContractUrl) } returns unwrapContract(expectedContractJwt)
        coEvery { mockedJwtValidator.verifySignature(any()) } returns true
        coEvery { linkedDomainsService.fetchDocumentAndVerifyLinkedDomains(any()) } returns KotlinResult.success(
            LinkedDomainUnVerified(mockedIdentifierDocumentServiceEndpoint)
        )
        coEvery { mockedResolver.resolve(expectedContract.input.issuer) } returns KotlinResult.success(mockedIdentifierDocument)

        runBlocking {
            val actualRequest = issuanceService.getRequest(suppliedContractUrl)
            assertThat(actualRequest).isInstanceOf(Result.Success::class.java)
            assertThat((actualRequest as Result.Success).payload.contractUrl).isEqualTo(suppliedContractUrl)
            assertThat(actualRequest.payload.linkedDomainResult).isInstanceOf(LinkedDomainUnVerified::class.java)
            assertThat(actualRequest.payload.entityName).isEqualTo(expectedEntityName)
            assertThat(actualRequest.payload.entityIdentifier).isEqualTo(expectedEntityIdentifier)
        }
    }

    @Test
    fun `test to get Issuance Request with linked domains verified`() {
        val suppliedContractUrl = "BusinessCard"
        val expectedEntityName = "Adatum Corporation"
        val expectedEntityIdentifier =
            "did:ion:EiCfeOciEjwupwRQsJC3wMZzz3_M3XIo6bhy7aJkCG6CAQ?-ion-initial-state=eyJkZWx0YV9oYXNoIjoiRWlEMDQwY2lQakUxR0xqLXEyWmRyLVJaXzVlcU8yNFlDMFI5bTlEd2ZHMkdGQSIsInJlY292ZXJ5X2NvbW1pdG1lbnQiOiJFaUMyRmQ5UE90emFNcUtMaDNRTFp0Wk43V0RDRHJjdkN4eTNvdlNERDhKRGVRIn0."

        coEvery { issuanceService["fetchContract"](suppliedContractUrl) } returns unwrapContract(expectedContractJwt)
        coEvery { mockedJwtValidator.verifySignature(any()) } returns true
        coEvery { linkedDomainsService.fetchDocumentAndVerifyLinkedDomains(any()) } returns KotlinResult.success(
            LinkedDomainVerified(mockedIdentifierDocumentServiceEndpoint)
        )
        coEvery { mockedResolver.resolve(expectedContract.input.issuer) } returns KotlinResult.success(mockedIdentifierDocument)
        every { mockedIdentifierDocument.service } returns listOf(mockedIdentifierDocumentService)
        every { mockedIdentifierDocumentService.type } returns mockedIdentifierDocumentServiceType
        every { mockedIdentifierDocumentService.serviceEndpoint } returns listOf(mockedIdentifierDocumentServiceEndpoint)

        runBlocking {
            val actualRequest = issuanceService.getRequest(suppliedContractUrl)
            assertThat(actualRequest).isInstanceOf(Result.Success::class.java)
            assertThat((actualRequest as Result.Success).payload.contractUrl).isEqualTo(suppliedContractUrl)
            assertThat(actualRequest.payload.linkedDomainResult).isInstanceOf(LinkedDomainVerified::class.java)
            assertThat((actualRequest.payload.linkedDomainResult as LinkedDomainVerified).domainUrl).isEqualTo(
                mockedIdentifierDocumentServiceEndpoint
            )
            assertThat(actualRequest.payload.entityName).isEqualTo(expectedEntityName)
            assertThat(actualRequest.payload.entityIdentifier).isEqualTo(expectedEntityIdentifier)
        }
    }

    @Test
    fun `test to send Issuance Response`() {
        val suppliedContractUrl = "BusinessCard"
        val issuanceRequest = IssuanceRequest(expectedContract, suppliedContractUrl, LinkedDomainMissing)
        val issuanceResponse = IssuanceResponse(issuanceRequest)
        val mockedPresentationAttestation = mockk<PresentationAttestation>()

        every {
            issuanceResponseFormatter.formatResponse(issuanceResponse.requestedVcMap.mapValues { expectedVerifiableCredential } as RequestedVcMap,
                issuanceResponse,
                masterIdentifier,
                DEFAULT_EXPIRATION_IN_SECONDS)
        } returns formattedResponse
        coEvery { identifierService.getIdentifierById(expectedVerifiableCredential.contents.sub) } returns Result.Success(masterIdentifier)
        every { mockedPresentationAttestation.credentialType } returns "TestCredentialType"
        every { mockedPresentationAttestation.validityInterval } returns 1000
        coEvery { issuanceService["sendResponse"](formattedResponse, issuanceResponse.audience) } returns Result.Success(expectedVerifiableCredential)

        runBlocking {
            val createdVerifiableCredential = issuanceService.sendResponse(issuanceResponse)
            assertThat(createdVerifiableCredential).isInstanceOf(Result.Success::class.java)
            assertThat((createdVerifiableCredential as Result.Success).payload.jti).isEqualTo(suppliedVcJti)
            assertThat(createdVerifiableCredential.payload.raw).isEqualTo(suppliedVcRaw)
            assertThat(createdVerifiableCredential.payload.contents).isEqualTo(suppliedVcContent)
        }

        verify(exactly = 1) {
            issuanceService["formAndSendResponse"](
                issuanceResponse,
                masterIdentifier,
                issuanceResponse.requestedVcMap.mapValues { expectedVerifiableCredential } as RequestedVcMap,
                DEFAULT_EXPIRATION_IN_SECONDS
            )
        }
    }

    private fun unwrapContract(jwsTokenString: String): VerifiableCredentialContract {
        val jwsToken = JwsToken.deserialize(jwsTokenString)
        return defaultTestSerializer.decodeFromString(VerifiableCredentialContract.serializer(), jwsToken.content())
    }
}

