package com.microsoft.walletlibrary.did.sdk.crypto.protocols.jose

import com.microsoft.walletlibrary.did.sdk.util.controlflow.ValidatorException
import org.assertj.core.api.Assertions
import org.assertj.core.api.AssertionsForInterfaceTypes.assertThat
import org.junit.Test

class JwaCryptoHelperTest {

    @Test
    fun `pass when # is present in jwt header kid`() {
        val kid = """did:ion:EiCZSL2UaBgXfH23f9NTySv0YV0aPlAOdTlqWKPp6y3xLA:#3274cddcb3e64258a9f7ce3854a4fc09vcSigningKey-a827e"""
        val expectedResult = Pair("""did:ion:EiCZSL2UaBgXfH23f9NTySv0YV0aPlAOdTlqWKPp6y3xLA:""", """3274cddcb3e64258a9f7ce3854a4fc09vcSigningKey-a827e""")
        val actualResult = JwaCryptoHelper.extractDidAndKeyId(kid)
        assertThat(actualResult).isEqualTo(expectedResult)
    }

    @Test
    fun `fail when # is present in jwt header kid`() {
        val kid = """did:ion:EiCZSL2UaBgXfH23f9NTySv0YV0aPlAOdTlqWKPp6y3xLA:-a827e"""
        try {
            JwaCryptoHelper.extractDidAndKeyId(kid)
        } catch (exception: Exception) {
            Assertions.assertThat(exception).isInstanceOf(ValidatorException::class.java)
        }
    }
}